SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION aacd_obter_min_data_adm_uti (nr_atendimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
	DTD - Data de admissão na UTI
	DTA - Data de alta na UTI
	NRP - Número de passagens na UTI

*/
ds_retorno_w	varchar(21);


BEGIN

if (ie_opcao_p = 'DTD') then
	select	to_char(min(dt_entrada_unidade),'dd/mm/yyyy hh24:mi:ss')
	into STRICT	ds_retorno_w
	from	atend_paciente_unidade
	where	nr_atendimento = nr_atendimento_p
	and	obter_classif_setor(cd_setor_atendimento) = 4;

elsif (ie_opcao_p = 'DTA') then
	select	to_char(min(dt_saida_unidade),'dd/mm/yyyy hh24:mi:ss')
	into STRICT	ds_retorno_w
	from	atend_paciente_unidade
	where	nr_atendimento = nr_atendimento_p
	and	obter_classif_setor(cd_setor_atendimento) = 4;

elsif (ie_opcao_p = 'NRP') then
	select	count(*)
	into STRICT	ds_retorno_w
	from	atend_paciente_unidade
	where	nr_atendimento = nr_atendimento_p
	and	obter_classif_setor(cd_setor_atendimento) = 4;

end	if;

return	ds_retorno_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
