SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION abrevia_nome ( nm_pessoa_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
ie_opcao_p - Para futuras customizações
*/
nm_pf_abrev_w		varchar(60);
nm_pessoa_fisica_w	varchar(80);
nm_sobrenome_w		varchar(80);

k			smallint;
i			smallint;


BEGIN

select	obter_initcap(nm_pessoa_p)
into STRICT	nm_pessoa_fisica_w
;

if (nm_pessoa_fisica_w IS NOT NULL AND nm_pessoa_fisica_w::text <> '') then
	begin

	k	:= position(' ' in nm_pessoa_fisica_w);

	if (k = 0) then
		nm_pf_abrev_w := nm_pessoa_fisica_w;
	else
		begin

		nm_pf_abrev_w := substr(nm_pessoa_fisica_w,1,k-1);

		for i in k..length(nm_pessoa_fisica_w) loop
			begin
			if (substr(nm_pessoa_fisica_w,i,1) = upper(substr(nm_pessoa_fisica_w,i,1))) then
				if (coalesce(nm_sobrenome_w::text, '') = '') then
					nm_sobrenome_w := substr(nm_pessoa_fisica_w,i,1);
				else
					nm_sobrenome_w := nm_sobrenome_w || substr(nm_pessoa_fisica_w,i,1);
				end if;
			end if;
			end;
		end loop;
		end;
	end if;
	end;
end if;

return	nm_pf_abrev_w || replace(nm_sobrenome_w,'  ',' ');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION abrevia_nome ( nm_pessoa_p text, ie_opcao_p text) FROM PUBLIC;

