CREATE OR REPLACE FUNCTION add_working_days ( dt_reference_p timestamp, qt_working_days_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_auxiliary_w		timestamp := null;
qt_working_days_w	bigint;
BEGIN
dt_auxiliary_w := dt_reference_p;
qt_working_days_w := qt_working_days_p;

if (qt_working_days_w IS NOT NULL AND qt_working_days_w::text <> '')  then
	if (qt_working_days_w < 0) then
		qt_working_days_w := qt_working_days_w * -1;

		for i in 1..qt_working_days_w loop
			dt_auxiliary_w := dt_auxiliary_w - 1;
			while	obter_se_dia_util(dt_auxiliary_w, obter_estabelecimento_ativo) = 'N' loop
				dt_auxiliary_w := dt_auxiliary_w - 1;
			end loop;
		end loop;
	elsif (qt_working_days_w > 0) then
		for i in 1..qt_working_days_w loop
			dt_auxiliary_w := dt_auxiliary_w + 1;
			while	obter_se_dia_util(dt_auxiliary_w, obter_estabelecimento_ativo) = 'N' loop
				dt_auxiliary_w := dt_auxiliary_w + 1;
			end loop;
		end loop;
	end if;

end if;

return	dt_auxiliary_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
