CREATE OR REPLACE FUNCTION adep_add_span_mat_comp (ds_informacao_p text) RETURNS varchar AS $body$
DECLARE


ds_span_w	varchar(2000);


BEGIN

	if ((trim(both ds_informacao_p) IS NOT NULL AND (trim(both ds_informacao_p))::text <> '')) then
		ds_span_w	:= chr(38)||'nbsp;'||chr(38)||'nbsp;'||chr(38)||'nbsp;<span class="ds-material-comp"> '||ds_informacao_p||'</span>';
	end if;

	return ds_span_w;

end;
