SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION adep_adm_alta_pac_minutos ( cd_convenio_p bigint, cd_setor_atendimento_p bigint, dt_alta_p timestamp, dt_evento_p timestamp) RETURNS varchar AS $body$
DECLARE



ie_permite_w 		adep_adm_alta_pac.ie_permite%type;	
qt_minutos_alta_w	adep_adm_alta_pac.qt_minutos_alta%type;
ds_retorno_w		varchar(10)	:= 'S';

C01 CURSOR FOR
SELECT	coalesce(ie_permite,'S'),
		coalesce(qt_minutos_alta,0)
from	adep_adm_alta_pac	
where	coalesce(cd_convenio,cd_convenio_p) = cd_convenio_p
and		coalesce(cd_setor_atendimento,cd_setor_atendimento_p) = cd_setor_atendimento_p
order by
		coalesce(cd_convenio,0),
		coalesce(cd_setor_atendimento,0),
		coalesce(qt_minutos_alta,0);
									

BEGIN

	open C01;
	loop
	fetch C01 into	
		ie_permite_w,
		qt_minutos_alta_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	
	--Verifica se a regra corrente está vigente.

	--Qt_minutos_alta é usado apenas nos casos em que o IE_PERMITE for "Sim"

	-- Caso IE_PERMITE = "Não", não é necessário comparar as datas 
	if	(qt_minutos_alta_w > 0 AND ie_permite_w = 'S') then
	    --Valida já passou dos minutos excedentes, se já passou retorna "N", se ainda está em tempo retorna "Sim".
		if (Obter_Min_Entre_Datas(dt_alta_p, dt_evento_p, 1) <= qt_minutos_alta_w) then		
			ds_retorno_w	:= 'S';
		else
			ds_retorno_w	:= 'N';
		end if;
	else
		ds_retorno_w	:= ie_permite_w;		
	end if;
	
	end loop;
	close C01;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
