CREATE OR REPLACE FUNCTION adep_hor_item_timeline (nr_seq_horario_p bigint, ie_tipo_item_p text, nr_prescricao_p bigint, nr_seq_solucao_p bigint, nr_etapa_sol_hor_p bigint) RETURNS timestamp AS $body$
DECLARE


ds_horario_w timestamp;
nr_seq_horario_pp bigint;


BEGIN

if (nr_seq_horario_p IS NOT NULL AND nr_seq_horario_p::text <> '') then 

    if (ie_tipo_item_p = 'D') then
	
		select max(coalesce(dt_horario, '')) 
		into STRICT ds_horario_w 
		from prescr_dieta_hor 
		where nr_sequencia = nr_seq_horario_p
		and Obter_se_horario_liberado(dt_lib_horario, dt_horario) = 'S';
  

	elsif (ie_tipo_item_p in ('S','M','MAT','IAG','IA', 'SNE','LD')) then 

		select    max(coalesce(dt_horario, ''))
		into STRICT    ds_horario_w 
		from    prescr_mat_hor
		where    nr_sequencia = nr_seq_horario_p
		and    Obter_se_horario_liberado(dt_lib_horario, dt_horario) = 'S';

	elsif (ie_tipo_item_p in ('P','G','C','I')) then

		select    max(coalesce(dt_horario, '')) 
		into STRICT    ds_horario_w 
		from    prescr_proc_hor 
		where    nr_sequencia    = nr_seq_horario_p 
		and    Obter_se_horario_liberado(dt_lib_horario, dt_horario) = 'S';

	elsif (ie_tipo_item_p = 'R') then 

		select    max(coalesce(dt_horario, '')) 
		into STRICT    ds_horario_w 
		from    prescr_rec_hor 
		where    nr_sequencia = nr_seq_horario_p 
		and    Obter_se_horario_liberado(dt_lib_horario, dt_horario) = 'S';

	elsif (ie_tipo_item_p in ('SOL')) then  

		nr_seq_horario_pp := obter_seq_hor_etapa_sol(nr_prescricao_p, nr_seq_solucao_p, nr_etapa_sol_hor_p);

		select  max(coalesce(dt_horario, '')) 
		into STRICT    ds_horario_w 
		from    prescr_mat_hor 
		where   nr_sequencia    = nr_seq_horario_pp 
		and    Obter_se_horario_liberado(dt_lib_horario, dt_horario) = 'S';

	elsif (ie_tipo_item_p = 'E') then 
		select    max(coalesce(dt_horario, ''))
		into STRICT    ds_horario_w 
		from    pe_prescr_proc_hor 
		where    nr_sequencia    = nr_seq_horario_p;

	end if;
end if;

return ds_horario_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
