CREATE OR REPLACE FUNCTION adep_obter_desc_ativ_interv ( nr_seq_proc_p bigint, nr_seq_prescr_p bigint default 0) RETURNS varchar AS $body$
DECLARE

 
ds_atividade_w		varchar(255)	:= null;
ds_atividades_w		varchar(2000)	:= null;
ie_registro_w		char(1);

c01 CURSOR FOR 
SELECT	substr(obter_desc_atividade_interv(nr_seq_atividade),1,255) 
from	pe_intervencao_atividade 
where	nr_seq_proc = nr_seq_proc_p;

c02 CURSOR FOR 
SELECT	substr(obter_desc_atividade_interv(nr_seq_atividade),1,255) 
from	pe_prescr_proc_atividade 
where	nr_seq_prescr_proc = nr_seq_prescr_p;


BEGIN 
 
 
select	coalesce(max('S'),'N') 
into STRICT	ie_registro_w 
from	pe_prescr_proc_atividade 
where	nr_seq_prescr_proc = nr_seq_prescr_p;
 
if (ie_registro_w = 'S') then 
	open c02;
	loop 
	fetch c02 into 
		ds_atividade_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */	
		begin 
 
		if (coalesce(length(ds_atividades_w),0) < 1740) then 
			ds_atividades_w	:= ds_atividades_w || ds_atividade_w || chr(10);
		end if;
	 
		end;
	end loop;
	close c02;
elsif (nr_seq_proc_p IS NOT NULL AND nr_seq_proc_p::text <> '') then 
	open c01;
	loop 
	fetch c01 into 
		ds_atividade_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */	
		begin 
 
		if (coalesce(length(ds_atividades_w),0) < 1740) then 
			ds_atividades_w	:= ds_atividades_w || ds_atividade_w || chr(10);
		end if;
	 
		end;
	end loop;
	close c01;
end if;
	 
return ds_atividades_w;
 
end;
$body$
