CREATE OR REPLACE FUNCTION adep_obter_horarios_adm ( nr_prescricao_p bigint, nr_seq_item_p bigint, ie_tipo_item_p text) RETURNS varchar AS $body$
DECLARE


ds_horarios_w           varchar(255);
dt_horario_w            varchar(255);

c01 CURSOR FOR
SELECT  to_char(dt_horario,'hh24:mi')
from    prescr_mat_hor
where   nr_prescricao   = nr_prescricao_p
and     nr_seq_material = nr_seq_item_p
and     (dt_fim_horario IS NOT NULL AND dt_fim_horario::text <> '')
and	Obter_se_horario_liberado(dt_lib_horario, dt_horario) = 'S';

c02 CURSOR FOR
SELECT  to_char(dt_horario,'hh24:mi')
from    prescr_proc_hor
where   nr_prescricao   	= nr_prescricao_p
and     nr_seq_procedimento	= nr_seq_item_p
and     (dt_fim_horario IS NOT NULL AND dt_fim_horario::text <> '')
and	Obter_se_horario_liberado(dt_lib_horario, dt_horario) = 'S';

c03 CURSOR FOR
SELECT  to_char(dt_horario,'hh24:mi')
from    prescr_rec_hor
where   nr_prescricao   	= nr_prescricao_p
and     nr_seq_recomendacao	= nr_seq_item_p
and     (dt_fim_horario IS NOT NULL AND dt_fim_horario::text <> '')
and	Obter_se_horario_liberado(dt_lib_horario, dt_horario) = 'S';


BEGIN

if (ie_tipo_item_p in ('S','M','MAT')) then

	open C01;
	loop
	fetch C01 into
		dt_horario_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		ds_horarios_w   := ds_horarios_w || ' ' || dt_horario_w;
	end loop;
	close C01;

elsif (ie_tipo_item_p in ('P','L')) then

	open C02;
	loop
	fetch C02 into
		dt_horario_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		ds_horarios_w   := ds_horarios_w || ' ' || dt_horario_w;
	end loop;
	close C02;

elsif (ie_tipo_item_p = 'R') then

	open C03;
	loop
	fetch C03 into
		dt_horario_w;
	EXIT WHEN NOT FOUND; /* apply on C03 */
		ds_horarios_w   := ds_horarios_w || ' ' || dt_horario_w;
	end loop;
	close C03;

end if;

return  ds_horarios_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
