CREATE OR REPLACE FUNCTION adep_obter_inclusao_regra ( ie_tipo_regra_p text, nr_prescricao_p bigint, cd_estabelecimento_p bigint, cd_setor_atendimento_p bigint, cd_perfil_p bigint, cd_material_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, nr_seq_proc_interno_p bigint, cd_setor_prescricao_p bigint, cd_setor_execucao_p bigint, nr_seq_exame_p bigint) RETURNS varchar AS $body$
DECLARE

	 			
cd_setor_atendimento_w	integer;
cd_perfil_w		integer;
				
ie_regra_inclusao_w	varchar(15)	:= 'S';
cd_grupo_material_w	smallint;
cd_subgrupo_material_w	smallint;
cd_classe_material_w	integer;

nr_seq_proc_interno_w	smallint;
cd_procedimento_w	bigint;
ie_origem_proced_w	bigint;
cd_area_procedimento_w	bigint;
cd_especialidade_w	bigint;
cd_grupo_proc_w		bigint;
cd_setor_prescricao_w	integer;
cd_setor_execucao_w	integer;
cd_setor_atual_w	integer;
ie_tipo_evolucao_w	varchar(15);
nr_atendimento_w	bigint;
nr_seq_atecaco_w	bigint;
cd_convenio_w		integer;
cd_categoria_w		varchar(10);
cd_plano_w		varchar(10);
cd_tipo_acomodacao_w	smallint;
ie_hemodialise_w	varchar(10);
nr_seq_exame_w		bigint;
ie_existe_regra_mat_w	varchar(1);
ie_existe_regra_proc_w	varchar(1);

c01 CURSOR FOR
SELECT	ie_regra_inclusao
from	adep_material
where	ie_tipo_regra = ie_tipo_regra_p
and (cd_estabelecimento = cd_estabelecimento_p)
and		((cd_setor_atendimento = cd_setor_atendimento_w) or (coalesce(cd_setor_atendimento::text, '') = ''))
and		((cd_perfil = cd_perfil_w) or (coalesce(cd_perfil::text, '') = ''))
and		((cd_material = cd_material_p) or (coalesce(cd_material::text, '') = ''))
and		((cd_grupo_material = cd_grupo_material_w) or (coalesce(cd_grupo_material::text, '') = ''))
and		((cd_subgrupo_material = cd_subgrupo_material_w) or (coalesce(cd_subgrupo_material::text, '') = ''))
and		((cd_classe_material = cd_classe_material_w) or (coalesce(cd_classe_material::text, '') = ''))
and		((cd_setor_prescr = cd_setor_prescricao_w) or (coalesce(cd_setor_prescr::text, '') = ''))
and 	((ie_tipo_evolucao = ie_tipo_evolucao_w) or (coalesce(ie_tipo_evolucao::text, '') = ''))
and		((cd_setor_atual = cd_setor_atual_w) or (coalesce(cd_setor_atual::text, '') = ''))
and		((coalesce(ie_especifica,'N') = 'N') or (ie_hemodialise_w IS NOT NULL AND ie_hemodialise_w::text <> ''))
and		coalesce(ie_situacao,'A') = 'A'
order by
	coalesce(cd_setor_atendimento,99999) desc,
	coalesce(cd_setor_prescr,99999) desc,
	coalesce(cd_perfil,99999) desc,
	coalesce(cd_material,999999) desc,
	coalesce(cd_grupo_material,999) desc,
	coalesce(cd_subgrupo_material,999) desc,
	coalesce(cd_classe_material,99999) desc,
	nr_sequencia;	
	
c02 CURSOR FOR
SELECT	ie_regra_inclusao
from	adep_regra_inclusao_proc
where (cd_estabelecimento = cd_estabelecimento_p)
and		((cd_setor_atendimento = cd_setor_atendimento_w) or (coalesce(cd_setor_atendimento::text, '') = ''))
and		((cd_perfil = cd_perfil_w) or (coalesce(cd_perfil::text, '') = ''))
and		((nr_seq_proc_interno	= nr_seq_proc_interno_p) or (coalesce(nr_seq_proc_interno::text, '') = ''))
and		((cd_procedimento = cd_procedimento_w) or (coalesce(cd_procedimento::text, '') = ''))
and		((ie_origem_proc = ie_origem_proced_w) or (coalesce(ie_origem_proc::text, '') = ''))
and		((cd_area_procedimento = cd_area_procedimento_w) or (coalesce(cd_area_procedimento::text, '') = ''))
and		((cd_especialidade = cd_especialidade_w) or (coalesce(cd_especialidade::text, '') = ''))
and		((cd_grupo_proc = cd_grupo_proc_w) or (coalesce(cd_grupo_proc::text, '') = ''))
and		((cd_setor_exec = cd_setor_execucao_w) or (coalesce(cd_setor_exec::text, '') = ''))
and		((nr_seq_exame = nr_seq_exame_p) or (coalesce(nr_seq_exame::text, '') = ''))
and 	((ie_tipo_evolucao = ie_tipo_evolucao_w) or (coalesce(ie_tipo_evolucao::text, '') = ''))
and		coalesce(ie_situacao,'A') = 'A'
order by
	coalesce(cd_setor_atendimento,99999) desc,
	coalesce(cd_setor_exec,99999) desc,
	coalesce(cd_perfil,99999) desc,
	coalesce(nr_seq_proc_interno, 9999999999) desc,		
	coalesce(cd_procedimento,999999999999999) desc,
	coalesce(ie_origem_proced,9) desc,
	coalesce(cd_area_procedimento, 99999999) desc,
	coalesce(cd_especialidade, 99999999) desc,
	coalesce(cd_grupo_proc,999999999999999) desc,	
	coalesce(nr_seq_exame, 99999999) desc,
	nr_sequencia;


BEGIN

if (ie_tipo_regra_p IS NOT NULL AND ie_tipo_regra_p::text <> '') and (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') then
	
	select	coalesce(max('S'),'N') ie_existe_regra
	into STRICT	ie_existe_regra_mat_w
	from	adep_material a
	where	a.cd_estabelecimento = cd_estabelecimento_p;
	
	select	coalesce(max('S'),'N') ie_existe_regra
	into STRICT	ie_existe_regra_proc_w
	from	adep_regra_inclusao_proc a
	where	a.cd_estabelecimento = cd_estabelecimento_p;

	if ((ie_existe_regra_mat_w <> 'N') and (ie_tipo_regra_p in ('SO','MED','MAT'))) or
		((ie_existe_regra_proc_w <> 'N') and (ie_tipo_regra_p in ('PROC', 'LAB'))) then
		Select	max(b.ie_tipo_evolucao),
			max(a.nr_atendimento),
			max(Obter_Unidade_Atendimento(a.nr_atendimento,'A','CS')),
			max(a.ie_hemodialise)
		into STRICT	ie_tipo_evolucao_w,
			nr_atendimento_w,
			cd_setor_atual_w,
			ie_hemodialise_w
		from	prescr_medica a,
			usuario b
		where 	a.cd_prescritor		= b.cd_pessoa_fisica
		and	a.nr_prescricao		= nr_prescricao_p
		and	coalesce(b.ie_situacao,'A') = 'A';
	
		nr_seq_atecaco_w := Obter_Atecaco_atendimento(nr_atendimento_w);
		
		select	max(cd_convenio),
				max(cd_categoria),
				max(cd_plano_convenio),
				max(cd_tipo_acomodacao)
		into STRICT	cd_convenio_w,
				cd_categoria_w,
				cd_plano_w,
				cd_tipo_acomodacao_w
		from	atend_categoria_convenio
		where	nr_seq_interno = nr_seq_atecaco_w;
	
		cd_setor_atendimento_w	:= coalesce(cd_setor_atendimento_p,0);
		cd_perfil_w		:= coalesce(cd_perfil_p,0);
		cd_setor_prescricao_w	:= coalesce(cd_setor_prescricao_p,0);
		cd_setor_execucao_w	:= coalesce(cd_setor_execucao_p,0);
	
		if (ie_tipo_regra_p in ('SO','MED','MAT')) then
		
			select	max(cd_grupo_material),
				max(cd_subgrupo_material),
				max(cd_classe_material)
			into STRICT	cd_grupo_material_w,
				cd_subgrupo_material_w,
				cd_classe_material_w
			from	estrutura_material_v
			where	cd_material = cd_material_p;
			
			open c01;
			loop
			fetch c01 into	ie_regra_inclusao_w;
			EXIT WHEN NOT FOUND; /* apply on c01 */
				begin
				ie_regra_inclusao_w	:= ie_regra_inclusao_w;
				end;
			end loop;
			close c01;
	
		elsif (ie_tipo_regra_p in ('PROC', 'LAB')) then
	
	
			if (nr_seq_proc_interno_p IS NOT NULL AND nr_seq_proc_interno_p::text <> '') then
				SELECT * FROM obter_proc_tab_interno_conv(nr_seq_proc_interno_p, cd_estabelecimento_p, cd_convenio_w, cd_categoria_w, cd_plano_w, null, cd_procedimento_w, ie_origem_proced_w, null, clock_timestamp(), cd_tipo_acomodacao_w, null, null, null, null, null, null, null) INTO STRICT cd_procedimento_w, ie_origem_proced_w;
			else
				cd_procedimento_w	:= cd_procedimento_p;
				ie_origem_proced_w	:= ie_origem_proced_p;
			end if;
								
			select 	max(cd_area_procedimento),
				max(cd_especialidade),
				max(cd_grupo_proc)
			into STRICT	cd_area_procedimento_w,
				cd_especialidade_w,
				cd_grupo_proc_w
			from	estrutura_procedimento_v
			where	cd_procedimento 	= cd_procedimento_w
			and	ie_origem_proced 	= ie_origem_proced_w;	
			
			open c02;
			loop
			fetch c02 into ie_regra_inclusao_w;
			EXIT WHEN NOT FOUND; /* apply on c02 */
				begin
				ie_regra_inclusao_w := ie_regra_inclusao_w;
				end;
			end loop;
			close c02;		
		end if;
	end if;
end if;

return ie_regra_inclusao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION adep_obter_inclusao_regra ( ie_tipo_regra_p text, nr_prescricao_p bigint, cd_estabelecimento_p bigint, cd_setor_atendimento_p bigint, cd_perfil_p bigint, cd_material_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, nr_seq_proc_interno_p bigint, cd_setor_prescricao_p bigint, cd_setor_execucao_p bigint, nr_seq_exame_p bigint) FROM PUBLIC;

