CREATE OR REPLACE FUNCTION adep_obter_outra_bolsa_prescr (nr_barras_p text, nr_prescricao_p bigint, nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w varchar(15) := null;
cont_w  bigint;
nr_bolsa_w varchar(50);
nr_sequencia_w numeric(20);


BEGIN

select 	count(*)
into STRICT 	cont_w
FROM 	san_reserva_prod a,
	san_producao b,
	san_reserva c
WHERE 	a.nr_seq_producao = b.nr_sequencia
AND   	a.nr_seq_reserva = c.nr_sequencia
AND 	c.nr_prescricao = nr_prescricao_p;

if (cont_w > 0) then

	select  max(a.nr_sequencia)
	into STRICT  	nr_sequencia_w
	FROM 	san_reserva_prod a,
		san_producao b,
		san_reserva c
	WHERE   a.nr_seq_producao = b.nr_sequencia
	AND	a.nr_seq_reserva = c.nr_sequencia
	AND 	c.nr_prescricao = nr_prescricao_p
	and 	((b.nr_sangue = nr_barras_p) or (to_char(a.nr_sequencia) = nr_barras_p) or (b.cd_barras = nr_barras_p));

IF (nr_sequencia_w > 0) THEN
	ds_retorno_w := nr_sequencia_w;
 END IF;

end if;

RETURN ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
