SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION adep_obter_prescr_perit (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


nr_prescr_w	varchar(2000) := '0';
nr_prescricao_w	bigint;

c01 CURSOR FOR
SELECT	nr_prescricao
from	prescr_medica
where	nr_atendimento = nr_atendimento_p
and	coalesce(ie_adep,'S') = 'S'
and	obter_se_prescr_vig_adep(dt_inicio_prescr, dt_validade_prescr, clock_timestamp(), clock_timestamp()) = 'S'
order by
	nr_prescricao;


BEGIN
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then

	open c01;
	loop
	fetch c01 into nr_prescricao_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		nr_prescr_w := nr_prescr_w || ',' || nr_prescricao_w;
		end;
	end loop;
	close c01;

end if;

return nr_prescr_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
