DECLARE


ie_liberado_w		varchar(1);
				

BEGIN

ie_liberado_w	:= 'N';

if (nr_seq_projeto_p IS NOT NULL AND nr_seq_projeto_p::text <> '') then
	select	coalesce(max('S'),'N')
