CREATE OR REPLACE FUNCTION adep_obter_se_bolsa_paciente2 (nr_barras_p text, nr_prescricao_p bigint, nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(15) := 'N';
cont_w		bigint;
nr_bolsa_w	varchar(50);
nr_sequencia_w	numeric(20);


BEGIN

select count(*)
into STRICT cont_w
FROM	san_reserva_prod a,
	san_producao b,
	san_reserva c
WHERE a.nr_seq_producao = b.nr_sequencia
AND	  a.nr_seq_reserva = c.nr_sequencia
AND	a.nr_sequencia = nr_sequencia_p
AND	c.nr_prescricao = nr_prescricao_p;

if (cont_w > 0) then
  begin
  select  count(*)
  into STRICT 	cont_w
  FROM	san_reserva_prod a,
    san_producao b,
    san_reserva c,
    prescr_proc_bolsa x
  WHERE	a.nr_seq_producao = b.nr_sequencia
  AND	a.nr_sequencia = nr_sequencia_p
  AND	c.nr_prescricao = nr_prescricao_p
  and x.nr_prescricao		= c.nr_prescricao
  and	coalesce(x.dt_cancelamento::text, '') = ''
  and	((b.nr_sangue = nr_barras_p) or (a.nr_sequencia = nr_barras_p) or (b.cd_barras = nr_barras_p) or (x.cd_bolsa = nr_barras_p));
  exception
      when data_exception then
      select  count(*)
      into STRICT 	cont_w
      FROM	san_reserva_prod a,
        san_producao b,
        san_reserva c,
        prescr_proc_bolsa x
      WHERE	a.nr_seq_producao = b.nr_sequencia
      AND	a.nr_sequencia = nr_sequencia_p
      AND	c.nr_prescricao = nr_prescricao_p
      and x.nr_prescricao		= c.nr_prescricao
      and	coalesce(x.dt_cancelamento::text, '') = ''
      and	((b.nr_sangue = nr_barras_p) or (b.cd_barras = nr_barras_p) or (x.cd_bolsa = nr_barras_p));
  end;
  IF (cont_w > 0) THEN
      ds_retorno_w	:= 'S';
    END IF;

end if;

RETURN	ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
