CREATE OR REPLACE FUNCTION adep_obter_se_bolsa_paciente (nr_bolsa_p text, nr_prescricao_p bigint, nr_seq_proced_p bigint, nr_seq_horario_p bigint) RETURNS varchar AS $body$
DECLARE


nr_bolsa_w      prescr_proc_bolsa.nr_bolsa%type;
nr_bolsa_aux_w      prescr_proc_bol_aux.nr_bolsa%type;
ie_situacao_w       prescr_proc_bol_aux.ie_situacao%type;

ds_retorno_w	varchar(10);


BEGIN

    select  coalesce(p.nr_bolsa, '0'),
            coalesce(a.nr_bolsa, '0'),
            coalesce(a.ie_situacao, '0')
    into STRICT    nr_bolsa_w,
            nr_bolsa_aux_w,
            ie_situacao_w
    FROM prescr_proc_bolsa p
LEFT OUTER JOIN prescr_proc_bol_aux a ON (p.nr_seq_horario = a.nr_seq_horario)
WHERE (p.nr_bolsa = nr_bolsa_p or (p.nr_bolsa = '0' and a.nr_bolsa = nr_bolsa_p and a.ie_situacao != 'I')) and p.nr_prescricao = nr_prescricao_p and coalesce(p.dt_cancelamento::text, '') = '' and p.nr_seq_procedimento	= nr_seq_proced_p and (((coalesce(nr_seq_horario_p ,0) > 0) and (p.nr_seq_horario = coalesce(nr_seq_horario_p ,0))) or (coalesce(nr_seq_horario_p ,0) = 0 and p.nr_seq_horario in (
                SELECT 	     min(nr_sequencia)
                from      	 prescr_proc_hor
                where        nr_prescricao = nr_prescricao_p
                and     	 coalesce(dt_fim_horario::text, '') = '' 
                and          coalesce(dt_suspensao::text, '') = ''
                and     	 nr_seq_procedimento = nr_seq_proced_p )
                ));

    if (nr_bolsa_w != '0' or (nr_bolsa_aux_w != '0' and ie_situacao_w = 'A')) then
         ds_retorno_w := 'S';
    elsif (nr_bolsa_aux_w != '0' and ie_situacao_w = 'L') then
        ds_retorno_w := 'L';
    else
        ds_retorno_w := 'N';
    end if;

    return	ds_retorno_w;

  exception
      when no_data_found then
      return 'N';

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
