CREATE OR REPLACE FUNCTION adep_obter_se_hor_duplic ( nr_atendimento_p bigint, nr_prescricao_p bigint, cd_item_p text, ie_origem_proced_p bigint, dt_horario_p timestamp, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
/* 
	S - Suplemento Oral 
	M - Medicamentos 
	P - Procedimentos 
	R - Recomendações 
	E - SAE	 
	L - Coletas 
*/
 
 
ds_duplic_w	varchar(1) :=	'N';


BEGIN 
 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (dt_horario_p IS NOT NULL AND dt_horario_p::text <> '') then 
 
	/*	Suplemento Oral	*/
 
	if (ie_opcao_p = 'S') then 
		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  
		into STRICT	ds_duplic_w 
		from	adep_pend_v a 
		where	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') 
		and	a.nr_atendimento	= nr_atendimento_p 
		and	a.dt_horario 		= dt_horario_p 
		and	a.nr_prescricao		<> nr_prescricao_p 
		and	a.cd_item		= cd_item_p 
		and	a.ie_tipo_item		= 'S' 
		and	exists (	SELECT	b.cd_item 
					from	adep_pend_v b 
					where	b.cd_item		= cd_item_p 
					and	b.nr_prescricao 	= nr_prescricao_p 
					and	b.ie_tipo_item		= 'S');
 
	/*	Medicamentos	*/
 
	elsif (ie_opcao_p = 'M') then 
		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  
		into STRICT	ds_duplic_w 
		from	adep_pend_v a 
		where	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') 
		and	a.nr_atendimento	= nr_atendimento_p 
		and	a.dt_horario 		= dt_horario_p 
		and	a.nr_prescricao		<> nr_prescricao_p 
		and	a.cd_item		= cd_item_p 
		and	a.ie_tipo_item		= 'M' 
		and	exists (	SELECT	b.cd_item 
					from	adep_pend_v b 
					where	b.cd_item		= cd_item_p 
					and	b.nr_prescricao 	= nr_prescricao_p 
					and	b.ie_tipo_item		= 'M');
 
	/*	Procedimentos	*/
 
	elsif (ie_opcao_p = 'P') then 
		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  
		into STRICT	ds_duplic_w 
		from	adep_pend_v a 
		where	a.nr_atendimento	= nr_atendimento_p 
		and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') 
		and	a.dt_horario 		= dt_horario_p 
		and	a.nr_prescricao		<> nr_prescricao_p 
		and	a.cd_item		= cd_item_p 
		and	a.ie_origem_proced	= ie_origem_proced_p 
		and	a.ie_tipo_item	= 'P' 
		and	exists (	SELECT	b.cd_item 
					from	adep_pend_v b 
					where	b.nr_prescricao		= nr_prescricao_p 
					and	b.cd_item		= cd_item_p 
					and	b.ie_origem_proced	= ie_origem_proced_p 
					and	b.ie_tipo_item	= 'P');
 
	/*	Recomendações	*/
 
	elsif (ie_opcao_p = 'R') then 
		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  
		into STRICT	ds_duplic_w 
		from	adep_pend_v a 
		where	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') 
		and	a.nr_atendimento	= nr_atendimento_p 
		and	a.dt_horario		= dt_horario_p 
		and	a.cd_item		= cd_item_p 
		and	a.nr_prescricao		<> nr_prescricao_p 
		and	a.ie_tipo_item		= 'R' 
		and	exists (	SELECT	b.cd_item 
					from	adep_pend_v b 
					where	b.cd_item	= cd_item_p 
					and	b.nr_prescricao	= nr_prescricao_p 
					and	b.ie_tipo_item	= 'R');
 
 
	/*	SAE	*/
 
	elsif (ie_opcao_p = 'E') then 
		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  
		into STRICT	ds_duplic_w 
		from	adep_pend_v a 
		where	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') 
		and	a.nr_atendimento	= nr_atendimento_p 
		and	a.dt_horario 		= dt_horario_p 
		and	a.cd_item		= cd_item_p 
		and	a.nr_prescricao		<> nr_prescricao_p 
		and	a.ie_tipo_item		= 'E' 
		and	exists (	SELECT	b.cd_item 
					from	adep_pend_v b 
					where	b.nr_prescricao		= nr_prescricao_p 
					and	b.cd_item		= cd_item_p 
					and	b.ie_tipo_item		= 'E');
	/*	Coletas	*/
 
	elsif (ie_opcao_p = 'L') then 
		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  
		into STRICT	ds_duplic_w 
		from	adep_pend_v a 
		where	a.nr_atendimento	= nr_atendimento_p 
		and	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') 
		and	a.dt_horario 		= dt_horario_p 
		and	a.nr_prescricao		<> nr_prescricao_p 
		and	a.cd_item		= cd_item_p 
		and	a.ie_origem_proced	= ie_origem_proced_p 
		and	a.ie_tipo_item	= 'L' 
		and	exists (	SELECT	b.cd_item 
					from	adep_pend_v b 
					where	b.nr_prescricao		= nr_prescricao_p 
					and	b.cd_item		= cd_item_p 
					and	b.ie_origem_proced	= ie_origem_proced_p 
					and	b.ie_tipo_item	= 'L');
	end if;
 
end if;
 
return	ds_duplic_w;	
 
end;
