CREATE OR REPLACE FUNCTION adep_obter_se_item_modelo ( ie_tipo_item_p text, nr_seq_modelo_p bigint, ie_hemodialise_p text) RETURNS varchar AS $body$
DECLARE


ie_item_modelo_w	varchar(255);
ie_tipo_item_w		varchar(20);


BEGIN

if (ie_tipo_item_p	= 'I') then
	ie_tipo_item_w	:= 'IVC';
elsif (ie_tipo_item_p	= 'C') then
	ie_tipo_item_w	:= 'G';
elsif (ie_tipo_item_p	= 'DE') then
	ie_tipo_item_w	:= 'D';
elsif (ie_tipo_item_p	= 'NPA') then
	ie_tipo_item_w	:= 'NAN';
elsif (ie_tipo_item_p	= 'MP') then
	ie_tipo_item_w	:= 'IA';
else
	ie_tipo_item_w	:= ie_tipo_item_p;
end if;

if (coalesce(ie_hemodialise_p,'N') <> 'N') then
	select	coalesce(max(ie_hemodialise),'S')
	into STRICT	ie_item_modelo_w
	from	adep_modelo_visualizacao
	where	nr_sequencia = nr_seq_modelo_p;

	if (ie_item_modelo_w = 'S') then
		select	coalesce(max('S'),'N')
		into STRICT	ie_item_modelo_w
		from	adep_modelo_visual_item
		where	nr_seq_modelo	= nr_seq_modelo_p
		and	ie_tipo_item	= ie_tipo_item_w;
	end if;
else
	if (ie_tipo_item_w = 'IAH') then
		select	coalesce(max('S'),'N')
		into STRICT	ie_item_modelo_w
		from	adep_modelo_visual_item
		where	nr_seq_modelo	= nr_seq_modelo_p
		and		ie_tipo_item	= 'M';
	else
	    select	coalesce(max('S'),'N')
		into STRICT	ie_item_modelo_w
		from	adep_modelo_visual_item
		where	nr_seq_modelo	= nr_seq_modelo_p
		and	ie_tipo_item	= ie_tipo_item_w;
	end if;
end if;

return	ie_item_modelo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
