CREATE OR REPLACE FUNCTION adep_obter_se_perm_dispensar (cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


ie_permite_w			varchar(1);
cd_classe_material_w		integer;
cd_subgrupo_material_w		integer;
cd_grupo_material_w		integer;


BEGIN

select	CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
into STRICT	ie_permite_w
from 	regra_disp_adep;

if (ie_permite_w = 'N') then

	select	Obter_estrutura_material(cd_material,'G'),
		Obter_estrutura_material(cd_material,'S'),
		cd_classe_material
	into STRICT	cd_grupo_material_w,
		cd_subgrupo_material_w,
		cd_classe_material_w
	from 	material
	where	cd_material = cd_material_p;

	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
	into STRICT	ie_permite_w
	from 	regra_disp_adep
	where 	coalesce(cd_material,cd_material_p) 				= cd_material_p
	and	coalesce(cd_grupo_material,cd_grupo_material_w) 		= cd_grupo_material_w
	and	coalesce(cd_subgrupo_material,cd_subgrupo_material_w)	= cd_subgrupo_material_w
	and	coalesce(cd_classe_material,cd_classe_material_w) 		= cd_classe_material_w;

end if;

return	ie_permite_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
