SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION adep_obter_status_gas_cpoe (nr_seq_gas_cpoe_p cpoe_gasoterapia.nr_sequencia%type, ie_exibir_hor_suspensos_p text default 'N') RETURNS varchar AS $body$
DECLARE

/*
 C - Codigo
D - Descricao 
*/
					
ie_status_w			prescr_gasoterapia_evento.ie_evento%type;
dt_liberacao_w		cpoe_gasoterapia.dt_liberacao%type;
qt_hor_pendente_w	bigint;
dt_suspensao_w		cpoe_gasoterapia.dt_suspensao%type;


BEGIN

select	max(cpoe_obter_dt_suspensao(a.nr_sequencia,'G')),
		max(a.dt_liberacao)
into STRICT	dt_suspensao_w,
		dt_liberacao_w
from	cpoe_gasoterapia	a
where	a.nr_sequencia	= nr_seq_gas_cpoe_p;

if (coalesce(dt_suspensao_w::text, '') = '' or ((dt_suspensao_w IS NOT NULL AND dt_suspensao_w::text <> '') and ie_exibir_hor_suspensos_p = 'S')) then
	select	max(ie_evento)
	into STRICT	ie_status_w
	from	prescr_gasoterapia_evento a
	where	a.nr_seq_gas_cpoe	= nr_seq_gas_cpoe_p
	and		coalesce(a.ie_evento_valido,'S') = 'S'
	and		a.ie_evento not in (SELECT x.cd_registro from table(lista_pck.obter_lista_char('V,AI,O,RV,IS,IM,RP,CR,SH')) x)
	and		a.nr_sequencia	= (	select	max(b.nr_sequencia)
								from	prescr_gasoterapia_evento b
								where	b.nr_seq_gas_cpoe	= nr_seq_gas_cpoe_p
								and		b.ie_evento not in (select x.cd_registro from table(lista_pck.obter_lista_char('V,AI,O,RV,IS,IM,RP,CR,SH')) x)
								and		coalesce(b.ie_evento_valido,'S') = 'S');

	if (coalesce(ie_status_w::text, '') = '') and (coalesce(dt_liberacao_w::text, '') = '') then
		ie_status_w := 'P';
	elsif (coalesce(ie_status_w::text, '') = '') then
		ie_status_w := 'N';
	elsif (ie_status_w = 'A') then
		ie_status_w := 'N';
	elsif (ie_status_w in ('SM','SS','SP')) then
		ie_status_w := 'S';
	elsif (ie_status_w = 'TE') then
		select	count(a.nr_sequencia)
		into STRICT	qt_hor_pendente_w
		from	prescr_gasoterapia b,
				prescr_gasoterapia_hor a
		where	a.nr_prescricao			= b.nr_prescricao
		and		a.nr_seq_gasoterapia	= b.nr_sequencia
		and		b.nr_seq_gas_cpoe		= nr_seq_gas_cpoe_p
		and		coalesce(a.dt_suspensao::text, '') = ''
		and		coalesce(a.dt_fim_horario::text, '') = '' LIMIT 1;
		
		if (qt_hor_pendente_w	= 0) then
			ie_status_w := 'T';
		end if;
	end if;

	if (ie_status_w = 'T' and
		(dt_suspensao_w IS NOT NULL AND dt_suspensao_w::text <> '') and
		(dt_liberacao_w IS NOT NULL AND dt_liberacao_w::text <> '') and
		clock_timestamp() > dt_suspensao_w) then
		ie_status_w := 'S';
	end if;
else
	ie_status_w	:= 'S';
end if;

return	ie_status_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION adep_obter_status_gas_cpoe (nr_seq_gas_cpoe_p cpoe_gasoterapia.nr_sequencia%type, ie_exibir_hor_suspensos_p text default 'N') FROM PUBLIC;

