CREATE OR REPLACE FUNCTION adicionar_semanas ( dt_referencia_p timestamp, nr_semanas_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_termino_semana_w	timestamp;
nr_semana_w		bigint := 0;
nr_controle_loop_w	smallint := 0;
dt_semana_w		timestamp;


BEGIN
if (dt_referencia_p IS NOT NULL AND dt_referencia_p::text <> '') and (nr_semanas_p IS NOT NULL AND nr_semanas_p::text <> '') and (nr_semanas_p > 0) then
	begin
	dt_termino_semana_w := obter_inicio_fim_semana(obter_inicio_fim_semana(dt_referencia_p,'I'),'F');

	while(nr_semana_w < nr_semanas_p) and (nr_controle_loop_w < 100) loop
		begin
		dt_semana_w		:= obter_inicio_fim_semana(dt_termino_semana_w + 1, 'I');
		dt_termino_semana_w	:= obter_inicio_fim_semana(dt_semana_w, 'F');

		nr_semana_w		:= nr_semana_w + 1;

		nr_controle_loop_w	:= nr_controle_loop_w + 1;
		end;
	end loop;
	end;
end if;
return dt_semana_w;
end;
$body$
