CREATE OR REPLACE FUNCTION adic_dias_uteis_horas_data ( dt_referencia_p timestamp, nr_horas_referencia_p bigint, nr_horas_adicionar_p bigint, cd_estabelecimento_p bigint) RETURNS timestamp AS $body$
DECLARE


nr_dias_adicionar_w	bigint;
nr_ctrl_loop_w		integer := 0;
ie_dia_util_w		varchar(1);
dt_dia_horas_adic_w	timestamp;


BEGIN
if (dt_referencia_p IS NOT NULL AND dt_referencia_p::text <> '') and (nr_horas_referencia_p IS NOT NULL AND nr_horas_referencia_p::text <> '') and (nr_horas_adicionar_p IS NOT NULL AND nr_horas_adicionar_p::text <> '') and (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') then
	begin
	dt_dia_horas_adic_w	:= trunc(dt_referencia_p);
	nr_dias_adicionar_w 	:= round(dividir(nr_horas_adicionar_p, nr_horas_referencia_p));

	while(nr_dias_adicionar_w > 0) and (nr_ctrl_loop_w < 10000) loop
		begin
		dt_dia_horas_adic_w	:= dt_dia_horas_adic_w + 1;
		ie_dia_util_w 		:= obter_se_dia_util(dt_dia_horas_adic_w, cd_estabelecimento_p);

		if (ie_dia_util_w = 'S') then
			begin
			nr_dias_adicionar_w := nr_dias_adicionar_w - 1;
			end;
		end if;
		nr_ctrl_loop_w := nr_ctrl_loop_w + 1;
		end;
	end loop;
	end;
end if;
return dt_dia_horas_adic_w;
end;
