SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION af_obter_ds_cor (nr_seq_status_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN

if (nr_seq_status_p IS NOT NULL AND nr_seq_status_p::text <> '') and (ie_opcao_p = 'V') then

	select	ds_cor
	into STRICT	ds_retorno_w
	from	status_atend_futuro
	where	nr_sequencia = nr_seq_status_p
	and	ie_situacao = 'A';

elsif (nr_seq_status_p IS NOT NULL AND nr_seq_status_p::text <> '') and (ie_opcao_p = 'A') then

	select	ds_cor_alerta
	into STRICT	ds_retorno_w
	from	status_atend_futuro
	where	nr_sequencia = nr_seq_status_p
	and	ie_situacao = 'A';

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
