CREATE OR REPLACE FUNCTION af_obter_proc_interno ( nr_seq_atend_futuro_p bigint) RETURNS varchar AS $body$
DECLARE


cd_procedimento_w	bigint;
nr_seq_proced_prev_w	bigint;

BEGIN

select 	max(nr_sequencia)
into STRICT	nr_seq_proced_prev_w
from   	atend_pac_proced_previsto
where  	ie_proc_principal 	= 'S'
and	nr_seq_atend_futuro 	= nr_seq_atend_futuro_p;

select 	max(nr_seq_proc_interno)
into STRICT	cd_procedimento_w
from   	atend_pac_proced_previsto
where  	nr_sequencia = nr_seq_proced_prev_w;

return	cd_procedimento_w;

end;
