SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION af_obter_qtd_dias_log_status (nr_sequencia_p bigint, dt_atualizacao_p timestamp) RETURNS bigint AS $body$
DECLARE


qt_retorno_w		bigint;
nr_sequencia_w		bigint;
dt_atualizacao_w	timestamp;


BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (dt_atualizacao_p IS NOT NULL AND dt_atualizacao_p::text <> '') then

	select  max(nr_sequencia)
	into STRICT	nr_sequencia_w
	from 	atend_futuro_status_hist
	where 	nr_seq_atend_futuro = nr_sequencia_p
	and		dt_atualizacao < dt_atualizacao_p;

	select	max(dt_atualizacao)
	into STRICT	dt_atualizacao_w
	from 	atend_futuro_status_hist
	where 	nr_sequencia = nr_sequencia_w;

	if (coalesce(dt_atualizacao_w::text, '') = '') then

		select	max(dt_atualizacao_nrec)
		into STRICT	dt_atualizacao_w
		from	atend_pac_futuro
		where 	nr_sequencia = nr_sequencia_p;

	end if;

	select	trunc(dt_atualizacao_p) - trunc(dt_atualizacao_w)
	into STRICT 	qt_retorno_w
	;

end if;

return	qt_retorno_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
