CREATE OR REPLACE FUNCTION af_obter_qtd_dias_status (nr_sequencia_p bigint, dt_atualizacao_nrec_p timestamp) RETURNS bigint AS $body$
DECLARE


dt_atualizacao_status_w	timestamp;
qt_dias_status_w		integer;
ds_retorno_w		integer;


BEGIN
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	select	max(dt_atualizacao)
	into STRICT	dt_atualizacao_status_w
	from	atend_futuro_status_hist
	where	nr_seq_atend_futuro = nr_sequencia_p;

	if (dt_atualizacao_status_w IS NOT NULL AND dt_atualizacao_status_w::text <> '') then
		qt_dias_status_w := TRUNC(clock_timestamp()) - trunc(dt_atualizacao_status_w);
	else
		qt_dias_status_w := TRUNC(clock_timestamp()) - trunc(dt_atualizacao_nrec_p);
	end if;

	ds_retorno_w := qt_dias_status_w;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
