CREATE OR REPLACE FUNCTION af_obter_se_status_vencido ( qt_dias_status_p bigint, nr_seq_status_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);
qt_dias_status_w	bigint;
qt_dias_alerta_w	bigint;


BEGIN
if (qt_dias_status_p IS NOT NULL AND qt_dias_status_p::text <> '') and (nr_seq_status_p IS NOT NULL AND nr_seq_status_p::text <> '') and (ie_opcao_p = 'V') then

	select	max(qt_dias_status)
	into STRICT	qt_dias_status_w
	from	status_atend_futuro
	where	nr_sequencia = nr_seq_status_p
	and	ie_situacao = 'A';

	if ( qt_dias_status_p > qt_dias_status_w ) then
		ds_retorno_w := 'V';
	else
		ds_retorno_w := 'A';
	end if;

elsif (qt_dias_status_p IS NOT NULL AND qt_dias_status_p::text <> '') and (nr_seq_status_p IS NOT NULL AND nr_seq_status_p::text <> '') and (ie_opcao_p = 'A') then

	select	max(qt_dias_alerta)
	into STRICT	qt_dias_alerta_w
	from	status_atend_futuro
	where	nr_sequencia = nr_seq_status_p
	and	ie_situacao = 'A';

	if ( qt_dias_status_p > qt_dias_alerta_w ) then
		ds_retorno_w := 'A';
	else
		ds_retorno_w := 'N';
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
