CREATE OR REPLACE FUNCTION agecons_obter_bloq_estagio_pls (nr_seq_agenda_p bigint) RETURNS varchar AS $body$
DECLARE


qt_bloq_w	smallint;
ds_retorno_w	varchar(1);

BEGIN

select	count(*)
into STRICT	qt_bloq_w
from	agenda_bloq_estagio_aut;

if (qt_bloq_w > 0) then
	begin
	select	count(*)
	into STRICT	qt_bloq_w
	from	agenda_bloq_estagio_aut
	where	ie_estagio = (	SELECT	max(b.ie_estagio)
				from	autorizacao_convenio a,
					pls_guia_plano b
				where	a.cd_autorizacao = b.cd_guia
				and	a.nr_seq_agenda_consulta = nr_seq_agenda_p );
	end;
end if;

if (qt_bloq_w > 0) then
	ds_retorno_w	:= 'S';
else
	ds_retorno_w	:= 'N';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
