SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION agecons_obter_cheg_quimio ( cd_agenda_p bigint, ie_classif_agendamento_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1) := 'N';
ie_chegada_w		varchar(1);
ie_chegada_classif_w	varchar(1);
						

BEGIN

if (cd_agenda_p IS NOT NULL AND cd_agenda_p::text <> '')then
	select	coalesce(a.ie_chegada_quimio, 'N')
	into STRICT	ie_chegada_w
	from	agenda a		
	where	a.cd_agenda	= cd_agenda_p;
	
	if (ie_chegada_w = 'S')then
		select	coalesce(a.ie_chegada_quimio, 'N')
		into STRICT	ie_chegada_classif_w
		from	agenda_classif a		
		where	a.cd_classificacao	= ie_classif_agendamento_p;
	end if;
	
end if;

if (ie_chegada_classif_w = 'S')then
	ie_retorno_w	:= 'S';

end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
