CREATE OR REPLACE FUNCTION agecons_obter_dur_classif ( ie_classif_agenda_p text, cd_estabelecimento_p bigint, cd_agenda_p bigint, dt_agenda_p timestamp) RETURNS bigint AS $body$
DECLARE

 
cd_medico_w	varchar(10);			
vl_retorno_w	bigint;
nr_seq_regra_w	bigint;
			
C01 CURSOR FOR 
	SELECT	nr_sequencia 
	from	ageint_regra_dur_classif 
	where	ie_classif_agenda				= ie_classif_agenda_p 
	and	coalesce(cd_estabelecimento, cd_Estabelecimento_p)	= cd_estabelecimento_p 
	and	coalesce(cd_pessoa_fisica, cd_medico_w)		= cd_medico_w 
	and (trunc(dt_agenda_p) >= dt_inicio_vigencia or coalesce(dt_inicio_vigencia::text, '') = '') 
	and (trunc(dt_agenda_p) <= dt_fim_vigencia or coalesce(dt_fim_vigencia::text, '') = '') 
	order by 1;
			

BEGIN 
 
select	max(cd_pessoa_fisica) 
into STRICT	cd_medico_w 
from	agenda 
where	cd_agenda = cd_agenda_p;
 
if (cd_medico_w IS NOT NULL AND cd_medico_w::text <> '') and (ie_classif_agenda_p IS NOT NULL AND ie_classif_agenda_p::text <> '') then 
	 
	open C01;
	loop 
	fetch C01 into	 
		nr_seq_regra_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		nr_seq_regra_w := nr_seq_regra_w;
		end;
	end loop;
	close C01;
	 
	if (nr_seq_regra_w > 0) then	 
		select	coalesce(max(nr_minuto_duracao),0) 
		into STRICT	vl_retorno_w 
		from	ageint_regra_dur_classif 
		where	nr_sequencia = nr_seq_regra_w;
	end if;
end if;
 
return	vl_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
