CREATE OR REPLACE FUNCTION agecons_obter_medico_ref ( cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE

  ds_retorno_w varchar(255);

BEGIN
  IF (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') THEN
    BEGIN
      SELECT obter_nome_medico(a.cd_medico, 'g')
      INTO STRICT ds_retorno_w
      FROM pessoa_fisica a
      WHERE a.cd_pessoa_fisica = cd_pessoa_fisica_p;
    EXCEPTION
    WHEN OTHERS THEN
      ds_retorno_w := '';
    END;
  END IF;
RETURN ds_retorno_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
