CREATE OR REPLACE FUNCTION agecons_obter_med_req ( cd_pessoa_fisica_p text, dt_atual_p timestamp) RETURNS varchar AS $body$
DECLARE




cd_medico_req_w	varchar(255);




BEGIN

IF (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') AND (dt_atual_p IS NOT NULL AND dt_atual_p::text <> '') THEN
	BEGIN

		SELECT	MAX(a.cd_medico_req)
		INTO STRICT	cd_medico_req_w
		FROM	agenda_consulta a
		WHERE	a.nr_sequencia = ((	SELECT 	 MAX(a.nr_sequencia)
						FROM	 agenda_consulta a
						WHERE	 a.cd_pessoa_fisica = cd_pessoa_fisica_p
						AND	 a.dt_agenda <= dt_atual_p
						AND	 (a.cd_medico_req IS NOT NULL AND a.cd_medico_req::text <> '')
						AND	 a.ie_status_agenda NOT IN ('C','L','B')));

	END;

END IF;

RETURN cd_medico_req_w;
END;
