CREATE OR REPLACE FUNCTION ageexa_obter_total_proc ( cd_agenda_p bigint, dt_agenda_p timestamp) RETURNS varchar AS $body$
DECLARE


qt_agenda_w		integer;
qt_agenda_adic_w	integer;
qt_retorno_w		bigint;
ds_retorno_W		varchar(255);


BEGIN
ds_retorno_w	:= wheb_mensagem_pck.get_texto(306817, null); -- Nenhum procedimento
select	count(*)
into STRICT	qt_agenda_w
from	agenda_paciente
where	cd_agenda = cd_agenda_p
and	dt_agenda between dt_agenda_p and trunc(dt_agenda_p) + 86399/86400
and	ie_status_agenda not in ('C', 'L', 'B', 'F', 'I', 'R')
and	(cd_procedimento IS NOT NULL AND cd_procedimento::text <> '');

select	sum(coalesce(qt_procedimento, 1))
into STRICT	qt_agenda_adic_w
from	agenda_paciente a,
	agenda_paciente_proc b
where	a.cd_agenda = cd_agenda_p
and	a.nr_sequencia	= b.nr_sequencia
and	dt_agenda between dt_agenda_p and trunc(dt_agenda_p) + 86399/86400
and	a.ie_status_agenda not in ('C', 'L', 'B', 'F', 'I', 'R');

qt_retorno_w	:= coalesce(qt_Agenda_w, 0) + coalesce(qt_agenda_adic_w, 0);

if (qt_retorno_w = 1) then
	ds_retorno_w	:= '1 ' || wheb_mensagem_pck.get_texto(306818, null); -- 1 Procedimento
elsif (qt_retorno_w > 1) then
	ds_retorno_w	:= to_char(qt_retorno_w) ||' '|| wheb_mensagem_pck.get_texto(306819, null); -- #@QT_RETORNO#@ Procedimentos
end if;

return	ds_retorno_w;

end;
$body$
