CREATE OR REPLACE FUNCTION ageint_consiste_marc_itens (nr_seq_ageint_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);

BEGIN

select	CASE WHEN count(*)=0 THEN  'S'  ELSE 'N' END
into STRICT	ds_retorno_w
from    agenda_integrada_item b
where   b.nr_seq_agenda_int	= nr_seq_ageint_p
and	coalesce(b.ie_regra,0) not in (1,2,5)
and	coalesce(b.ie_glosa,'X') not in ('T','E','R','B','H','Z')
and	not exists (	SELECT	1
			from	ageint_marcacao_usuario x
			where	x.nr_seq_ageint_item	= b.nr_sequencia);

return	ds_retorno_w;

end;
$body$
