CREATE OR REPLACE FUNCTION ageint_consiste_tipo_agenda (nr_seq_ageint_p bigint, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);


BEGIN

if (ie_tipo_p = 'CC') then
	select	CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	agenda_integrada_item
	where	nr_seq_agenda_int	= nr_seq_ageint_p
	and	ie_tipo_agendamento = 'C';
end if;

if (ie_tipo_p = 'DC') then
	select	CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END
	into STRICT	ds_retorno_w
	from	agenda_integrada_item x
	where	nr_seq_agenda_int	= nr_seq_ageint_p
	and	ie_tipo_agendamento <> 'C';
end if;

return	ds_retorno_w;

end;
