CREATE OR REPLACE FUNCTION ageint_consiste_turno_ex_adic ( nr_seq_regra_p bigint, dt_agenda_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1)	:= 'S';
qt_regra_turno_w	bigint;			
ie_dia_semana_w		integer;
			

BEGIN
-- caso mexer nessa function verificar tambem get_Consiste_Turno_Ex_Adic na ageint_sugerir_horarios_pck
if (nr_seq_regra_p	> 0) then
	select	count(*)
	into STRICT	qt_regra_turno_w
	from	agecons_turno_regra_proc
	where	nr_seq_regra_proc	= nr_Seq_regra_p;

	if (qt_regra_turno_w > 0) then
		ie_dia_semana_w := ageint_sugerir_horarios_pck.get_Obter_Cod_Dia_Semana(dt_agenda_p);
		select	count(*)
		into STRICT	qt_regra_turno_w
		from	agecons_turno_regra_proc
		where	nr_seq_regra_proc	= nr_seq_regra_p
		and	dt_agenda_p	>= to_date(to_char(dt_agenda_p,'dd/mm/yyyy') || ' ' ||to_char(hr_inicial,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss')
		and	dt_agenda_p	<= to_date(to_char(dt_agenda_p,'dd/mm/yyyy') || ' ' ||to_char(hr_final,'hh24:mi:ss'),'dd/mm/yyyy hh24:mi:ss')
		and	((ie_dia_semana = ie_dia_semana_w) or (ie_dia_semana = 9));
		if (qt_regra_turno_w = 0) then
			ds_retorno_w	:= 'N';
		end if;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
