CREATE OR REPLACE FUNCTION ageint_obter_agrup_agenda ( nr_seq_proc_interno_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE


nr_seq_agrupamento_w	agrupamento_agenda.nr_sequencia%type;
cd_area_procedimento_w	area_procedimento.cd_area_procedimento%type;
cd_especialidade_w		especialidade_proc.cd_especialidade%type;
cd_grupo_proc_w			grupo_proc.cd_grupo_proc%type;

C01 CURSOR FOR
	SELECT	nr_seq_agrupamento
	from	AGEINT_AGRUP_AGENDA_ITEM
	where	ie_situacao	= 'A'
	and		coalesce(cd_estabelecimento, cd_estabelecimento_p)		= cd_estabelecimento_p
	and		coalesce(nr_Seq_proc_interno, nr_seq_proc_interno_p) 	= nr_Seq_proc_interno_p
	and		coalesce(cd_procedimento, cd_procedimento_p)				= cd_procedimento_p
	and		((coalesce(ie_origem_proced, ie_origem_proced_p)			= ie_origem_proced_p) or coalesce(cd_procedimento::text, '') = '')
	and		coalesce(cd_grupo_proc, cd_grupo_proc_w)					= cd_grupo_proc_w
	and		coalesce(cd_especialidade, cd_especialidade_w)			= cd_especialidade_w
	and		coalesce(cd_area_procedimento, cd_area_procedimento_w)	= cd_area_procedimento_w
	order by coalesce(cd_procedimento,0),
			coalesce(nr_seq_proc_interno,0),
			coalesce(cd_grupo_proc,0),
			coalesce(cd_especialidade,0),
			coalesce(cd_area_procedimento,0);


BEGIN

begin
select	cd_area_procedimento,
		cd_especialidade,
		cd_grupo_proc
into STRICT	cd_area_procedimento_w,
		cd_especialidade_w,
		cd_grupo_proc_w
from	estrutura_procedimento_v
where	cd_procedimento		= cd_procedimento_p
and		ie_origem_proced	= ie_origem_proced_p;
exception
when others then
	cd_area_procedimento_w	:= 0;
	cd_especialidade_w		:= 0;
	cd_grupo_proc_w			:= 0;
end;

open C01;
loop
fetch C01 into
	nr_seq_agrupamento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	nr_seq_agrupamento_w	:= nr_seq_agrupamento_w;
	end;
end loop;
close C01;

return nr_seq_agrupamento_w;

end;
$body$
