CREATE OR REPLACE FUNCTION ageint_obter_bloq_checklist (nr_seq_ageint_p bigint) RETURNS varchar AS $body$
DECLARE


ie_bloqueio_w	varchar(1);


BEGIN

select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_bloqueio_w
from	ageint_item_check_list b,
	ageint_check_list_pac_item a,
	ageint_check_list_paciente c
where	a.nr_seq_item_check_list = b.nr_sequencia
and	a.nr_seq_check_list = c.nr_sequencia
and	coalesce(b.ie_bloqueia_agendamento,'N') = 'S'
and	a.ds_resultado = 'S'
and	coalesce(c.ie_tipo_check_list,'I') = 'A'
and	c.nr_seq_ageint = nr_seq_ageint_p;

return	ie_bloqueio_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
