CREATE OR REPLACE FUNCTION ageint_obter_dados_agendamento ( nr_Seq_Agenda_p bigint, cd_agenda_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w	varchar(100);
cd_tipo_agenda_w varchar(10);


BEGIN 
 
/* IE_OPCAO_P 
 
UA	- Usuário acesso 
DC	- Data confirmação 
 
*/
 
select max(cd_tipo_agenda) 
into STRICT cd_tipo_agenda_w 
from agenda 
where cd_agenda = cd_agenda_p;
 
if (cd_tipo_agenda_w in (2)) then 
 
	if (ie_opcao_p	= 'UA') then 
		select	max(nm_usuario_acesso) 
		into STRICT	ds_retorno_w 
		from	agenda_paciente 
		where	nr_sequencia	= nr_Seq_Agenda_p;
 
	elsif (ie_opcao_p	= 'DC') then 
		select	to_char(max(dt_confirmacao)) 
		into STRICT	ds_retorno_w 
		from	agenda_paciente 
		where	nr_sequencia	= nr_Seq_Agenda_p;
	end if;
 
elsif (cd_tipo_agenda_w in (3,4,5)) then 
 
	if (ie_opcao_p	= 'UA') then 
		select	max(nm_usuario_acesso) 
		into STRICT	ds_retorno_w 
		from	agenda_consulta 
		where	nr_sequencia	= nr_Seq_Agenda_p;
	elsif (ie_opcao_p	= 'DC') then 
		select	to_char(max(dt_confirmacao)) 
		into STRICT	ds_retorno_w 
		from	agenda_consulta 
		where	nr_sequencia	= nr_Seq_Agenda_p;
	end if;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
