CREATE OR REPLACE FUNCTION ageint_obter_dados_agenda_ant ( cd_pessoa_fisica_p text, dt_agenda_p timestamp, ds_param1_p text default null, ds_param2_p text default null, ie_opcao_p text DEFAULT NULL) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
nr_seq_agenda_ant_w	bigint;


BEGIN

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_agenda_ant_w
from	agenda_integrada
where	dt_inicio_agendamento	< dt_agenda_p
and	cd_pessoa_fisica	= cd_pessoa_fisica_p;

if (nr_seq_agenda_ant_w	> 0) then
	if (ie_opcao_p	= 'P') then
		select	max(qt_peso)
		into STRICT	ds_retorno_w
		from	agenda_integrada
		where	nr_sequencia	 = nr_seq_Agenda_ant_w;
	elsif (ie_opcao_p	= 'A') then
		select	max(qt_altura_cm)
		into STRICT	ds_retorno_w
		from	agenda_integrada
		where	nr_sequencia	 = nr_seq_Agenda_ant_w;
	end if;
end if;

return	ds_retorno_w;

end;
