SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION ageint_obter_dados_conv ( nr_seq_ageint_p bigint, ie_retorno_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
ds_convenio_w		varchar(255);
ds_categoria_w		varchar(255);
ds_plano_w		varchar(255);
nr_doc_conv_w		varchar(20);
cd_usu_conv_w		varchar(30);
dt_val_carteira_w		varchar(20);

/*
DC = ds convenio
C = ds categoria
DP = ds plano
G = guia
CD = cod usuario
VL = validade
*/
BEGIN

ds_retorno_w := '';
ds_convenio_w := '';
ds_categoria_w := '';
ds_plano_w := '';
nr_doc_conv_w := '';
cd_usu_conv_w := '';
dt_val_carteira_w := '';

if (nr_seq_ageint_p IS NOT NULL AND nr_seq_ageint_p::text <> '') and (nr_seq_ageint_p <> 0) then
	select 	max(substr(obter_desc_convenio(cd_convenio),1,255)),
		max(substr(obter_categoria_convenio(cd_convenio,cd_categoria),1,255)),
		max(substr(obter_desc_plano_conv(cd_convenio,cd_plano),1,255)),
		max(nr_doc_convenio),
		max(cd_usuario_convenio),
		max(to_char(dt_validade_carteira,'dd/mm/yyyy'))
	into STRICT	ds_convenio_w,
		ds_categoria_w,
		ds_plano_w,
		nr_doc_conv_w,
		cd_usu_conv_w,
		dt_val_carteira_w
	from	agenda_integrada a
	where	a.nr_sequencia = nr_seq_ageint_p;
end if;

if (ie_retorno_p = 'DC') then
	ds_retorno_w 	:= ds_convenio_w;
elsif (ie_retorno_p = 'C') then
	ds_retorno_w 	:= ds_categoria_w;
elsif (ie_retorno_p = 'DP') then
	ds_retorno_w 	:= ds_plano_w;
elsif (ie_retorno_p	= 'G') then
	ds_retorno_w	:= nr_doc_conv_w;
elsif (ie_retorno_p	= 'CD') then
	ds_retorno_w	:= cd_usu_conv_w;
elsif (ie_retorno_p	= 'VL') then
	ds_retorno_w	:= dt_val_carteira_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
