SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION ageint_obter_dados_quimio ( nr_seq_pendencia_p bigint, ie_retorno_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w         varchar(255);
dt_data_agenda_w     varchar(50);
ie_status_w          varchar(10);
nr_sequencia_w       varchar(10);
cd_profissional_w    varchar(10);

/*o
S =  nr_seq_status
DI =  dt_inicio_agendamento
SE = nr_sequencia
*/
BEGIN

ds_retorno_w := '';
dt_data_agenda_w := '';
ie_status_w := '';

select  max(nr_seq_status),
		to_char(max(dt_inicio_agendamento),'dd/mm/yyyy hh24:mi:ss'),
		max(a.nr_sequencia),
		max(a.cd_profissional)
into STRICT 	ie_status_w,
		dt_data_agenda_w,
		nr_sequencia_w,
		cd_profissional_w
from 	agenda_integrada a,
		agenda_integrada_item b
where   a.nr_sequencia          = b.nr_seq_agenda_int
and  	b.nr_seq_pend_quimio    = nr_seq_pendencia_p;

if (ie_retorno_p = 'S') then
    ds_retorno_w := ie_status_w;
elsif (ie_retorno_p = 'DI') then
    ds_retorno_w := dt_data_agenda_w;
elsif (ie_retorno_p = 'SE') then
    ds_retorno_w := nr_sequencia_w;
elsif (ie_Retorno_p   = 'P') then
    ds_retorno_w    := cd_profissional_w;
elsif (ie_retorno_p = 'DT') then
    ds_retorno_w := dt_data_agenda_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
