CREATE OR REPLACE FUNCTION ageint_obter_dados_regiao (cd_estabelecimento_p bigint default null, opcao_p text DEFAULT NULL) RETURNS varchar AS $body$
DECLARE

						
ds_retorno_w varchar(255);
						   				

BEGIN 
	
	IF (opcao_p = 'CD') THEN 
	
		select max(wl.nr_sequencia) 
		into STRICT   ds_retorno_w
		from   wsuite_grupo_loc_estab we, 
		   	   wsuite_grupo_localizacao wl 
		where  we.cd_estabelecimento = cd_estabelecimento_p
		and    wl.nr_sequencia = we.nr_grupo_local
		and    wl.ie_situacao = 'A'
		and (coalesce(wl.ie_utilizacao, 'A') = 'A'
		or         wl.ie_utilizacao = 'T');
	
	ELSIF (opcao_p = 'DS') THEN
		
		select max(wl.ds_grupo_estab) 
		into STRICT   ds_retorno_w
		from   wsuite_grupo_loc_estab we, 
		   	   wsuite_grupo_localizacao wl 
		where  we.cd_estabelecimento = cd_estabelecimento_p
		and    wl.nr_sequencia = we.nr_grupo_local
		and    wl.ie_situacao = 'A'
		and (coalesce(wl.ie_utilizacao, 'A') = 'A'
		or         wl.ie_utilizacao = 'T');
	
	ELSE
		ds_retorno_w := null;
	
	END IF;

RETURN ds_retorno_w;	
	
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
