CREATE OR REPLACE FUNCTION ageint_obter_data_chegada ( nr_seq_Ageint_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_retorno_w		timestamp;
nr_seq_proc_interno_w	bigint;
qt_min_preparo_w	bigint;


BEGIN

select	min(obter_horario_item_ageint(nr_seq_agenda_cons, nr_seq_agenda_exame,nr_sequencia)),
	min(nr_seq_proc_interno)
into STRICT	dt_retorno_w,
	nr_seq_proc_interno_w
from	agenda_integrada_item
where	nr_seq_agenda_int = nr_seq_ageint_p
and ((nr_seq_agenda_exame IS NOT NULL AND nr_seq_agenda_exame::text <> '')
or	(nr_seq_agenda_cons IS NOT NULL AND nr_seq_agenda_cons::text <> ''));

select	coalesce(max(qt_min_preparo),0)
into STRICT	qt_min_preparo_w
from	proc_interno
where	nr_sequencia	= nr_seq_proc_interno_w;

if (qt_min_preparo_w	> 0) then
	dt_retorno_w	:= dt_retorno_w - qt_min_preparo_w / 1440;
end if;

return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
