CREATE OR REPLACE FUNCTION ageint_obter_desc_item_grupo (nr_seq_item_grupo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN

select  substr(coalesce(g.nm_fantasia_estab,obter_desc_expressao(777052)) || ' - '||coalesce(c.nm_curto,coalesce(d.ds_proc_exame,f.ds_especialidade)),1,255)
into STRICT	ds_retorno_w
FROM agenda_int_grupo b, agenda_int_area a
LEFT OUTER JOIN estabelecimento g ON (a.cd_estabelecimento = g.cd_estabelecimento)
, agenda_int_grupo_item c
LEFT OUTER JOIN proc_interno d ON (c.nr_seq_proc_interno = d.nr_sequencia)
LEFT OUTER JOIN especialidade_medica f ON (c.cd_especialidade = f.cd_especialidade)
LEFT OUTER JOIN pessoa_fisica e ON (c.cd_medico = e.cd_pessoa_fisica)
WHERE a.nr_sequencia        	= b.nr_seq_area and b.nr_sequencia        	= c.nr_seq_grupo     and c.nr_sequencia  	= nr_seq_item_grupo_p;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
