CREATE OR REPLACE FUNCTION ageint_obter_desc_tempo_item ( ie_opcao_p text, ie_duracao_item_marc_p text, nr_seq_ageint_p bigint, nr_seq_ageint_item_p bigint, cd_agenda_p bigint, cd_medico_p text) RETURNS varchar AS $body$
DECLARE

 
/* 
FUNCTION UTILIZADA NO WPANEL da Agenda Integrada no HTML5. 
Utilizado no Eixo do Item e da Agenda, conforme ie_opcao 
 
ie_opcao_p 
'I' - Item 
'A' - Agenda 
*/
				 
				 
ds_retorno_w		varchar(255);		
nr_minuto_duracao_w	bigint;
ie_lado_w		agenda_integrada_item.ie_lado%type;
nr_seq_agenda_exame_w	agenda_integrada_item.nr_seq_agenda_exame%type;
cd_convenio_w		agenda_integrada.cd_convenio%type;
cd_categoria_w		agenda_integrada.cd_categoria%type;
cd_plano_w		agenda_integrada.cd_plano%type;
nr_seq_proc_interno_w	agenda_integrada_item.nr_seq_proc_interno%type;
cd_procedimento_w	agenda_integrada_item.cd_procedimento%type;
ie_origem_proced_w	agenda_integrada_item.ie_origem_proced%type;
				

BEGIN 
 
select	max(b.nr_minuto_duracao),	 
	max(b.ie_lado), 
	max(a.cd_convenio), 
	max(a.cd_categoria), 
	max(a.cd_plano), 
	max(b.nr_seq_agenda_exame), 
	max(b.nr_seq_proc_interno), 
	max(b.cd_procedimento), 
	max(b.ie_origem_proced) 
into STRICT	nr_minuto_duracao_w, 
	ie_lado_w, 
	cd_convenio_w, 
	cd_categoria_w, 
	cd_plano_w, 
	nr_seq_agenda_exame_w, 
	nr_seq_proc_interno_w, 
	cd_procedimento_w, 
	ie_origem_proced_w 
from	agenda_integrada_item b, 
	agenda_integrada a 
where	a.nr_sequencia	= b.nr_seq_agenda_int 
and	a.nr_sequencia	= nr_seq_ageint_p 
and	b.nr_sequencia	= nr_seq_ageint_item_p;
 
ds_retorno_w := null;
 
if (coalesce(ie_duracao_item_marc_p,'N') = 'S') and (ie_opcao_p = 'I') then	 
	 
	if (nr_minuto_duracao_w > 0) then 
		 
		select	wheb_mensagem_pck.get_texto(495650)||' '||to_char(nr_minuto_duracao_w) 
		into STRICT	ds_retorno_w 
		;
	 
	end if;
 
elsif (coalesce(ie_duracao_item_marc_p,'N') = 'A') and (ie_opcao_p = 'A') then 
 
	nr_minuto_duracao_w := obter_tempo_padrao_ageint(	nr_seq_proc_interno_w, cd_procedimento_w, ie_origem_proced_w, null, cd_agenda_p, cd_medico_p, nr_minuto_duracao_w, ie_lado_w, cd_convenio_w, cd_categoria_w, cd_plano_w, nr_seq_agenda_exame_w, null);
					 
	if (nr_minuto_duracao_w > 0) then 
		 
		select	wheb_mensagem_pck.get_texto(495650)||' '||to_char(nr_minuto_duracao_w) 
		into STRICT	ds_retorno_w 
		;
	 
	end if;
 
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
