CREATE OR REPLACE FUNCTION ageint_obter_dias_atend_ans (nr_seq_proc_interno_p bigint, cd_especialidade_p bigint, ie_tipo_agendamento_p text) RETURNS bigint AS $body$
DECLARE

			
nr_dias_w	smallint;


BEGIN


if (ie_tipo_agendamento_p = 'C') then
	select	max(qt_dias_atend)
	into STRICT	nr_dias_w
	from	ageint_tempo_atend_ans
	where	cd_especialidade = cd_especialidade_p;
	
	if (coalesce(nr_dias_w::text, '') = '') then
		nr_dias_w := OBTER_QT_DIAS_CONS_ANS(cd_especialidade_p);
		
		if (nr_dias_w = 0) then
			nr_dias_w := null;
		end if;
	end if;
	
else
	select	max(qt_dias_atend)
	into STRICT	nr_dias_w
	from	ageint_tempo_atend_ans
	where	nr_seq_proc_interno = nr_seq_proc_interno_p;
	
end if;

return	nr_dias_w;

end;
$body$
