CREATE OR REPLACE FUNCTION ageint_obter_dur_classif ( ie_classif_agenda_p text, cd_estabelecimento_p bigint, cd_medico_p text default null, dt_agendamento_p timestamp default null) RETURNS bigint AS $body$
DECLARE


vl_retorno_w	bigint;
			
			
	
			
  c RECORD;

BEGIN


if (ie_classif_agenda_p IS NOT NULL AND ie_classif_agenda_p::text <> '') then

	for c in (SELECT nr_minuto_duracao
				from	ageint_regra_dur_classif
				where	ie_classif_agenda				= ie_classif_agenda_p
				and	coalesce(cd_estabelecimento, cd_Estabelecimento_p)	= cd_estabelecimento_p
				and	coalesce(cd_pessoa_fisica, coalesce(cd_medico_p,'XPTO'))	= coalesce(cd_medico_p,'XPTO')
				and (trunc(coalesce(dt_agendamento_p, clock_timestamp())) >= dt_inicio_vigencia or coalesce(dt_inicio_vigencia::text, '') = '')
				and (trunc(coalesce(dt_agendamento_p, clock_timestamp())) <= dt_fim_vigencia or coalesce(dt_fim_vigencia::text, '') = '')
				order by cd_pessoa_fisica desc) loop
				
					vl_retorno_w := c.nr_minuto_duracao;			
	end loop;
	
	vl_retorno_w := coalesce(vl_retorno_w,0);
	
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
