qt_grupo_w		integer;
nr_duracao_w		bigint;
ie_duracao_w		varchar(1);

BEGIN
ie_duracao_w := Obter_Param_Usuario(869, 340, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_duracao_w);

if (ie_duracao_w = 'N') then
	return null;
end if;

/* SQL performance */

begin
select	1
into STRICT	qt_grupo_w
from	agenda_int_grupo_item a,
	agenda_int_grupo b,
	agenda_int_area c
where	a.nr_seq_grupo = b.nr_sequencia
and	c.nr_sequencia = b.nr_seq_area
and	a.nr_seq_proc_interno = nr_seq_proc_interno_p
