exception
when no_data_found then
	qt_grupo_w := 0;
when others then
	qt_grupo_w := 2;
end;

if (qt_grupo_w = 1) then
	select	coalesce(max(b.nr_minuto_duracao), null)
	into STRICT	nr_duracao_w
	from	agenda_int_grupo a,
		agenda_int_grupo_item b,
		agenda_int_area d
	where	d.nr_sequencia		= a.nr_seq_area
	and	a.nr_sequencia		= b.nr_seq_grupo
	and	a.ie_situacao		= 'A'
	and	b.nr_seq_proc_interno	= nr_seq_proc_interno_p
	and	d.cd_estabelecimento	= cd_estabelecimento_p;
else
	nr_duracao_w	:= null;
end if;
return	nr_duracao_w;
end;
