SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION ageint_obter_email_comp (nr_seq_ageint_p bigint, ie_tipo_compl_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_Retorno_w			varchar(4000);		
nr_seq_ageint_item_w	bigint;		
ie_tipo_compl_w			smallint;
nr_seq_agenda_cons_w	agenda_integrada_item.nr_seq_agenda_cons%type;
nr_seq_agenda_exame_w	agenda_integrada_item.nr_seq_Agenda_exame%type;
ds_email_w				varchar(255);
	
C01 CURSOR FOR
	SELECT	nr_sequencia,	
			nr_seq_agenda_cons,
			nr_seq_Agenda_exame
	from	agenda_integrada_item
	where	nr_Seq_agenda_int	= nr_seq_Ageint_p
	and ((nr_seq_agenda_cons IS NOT NULL AND nr_seq_agenda_cons::text <> '')
	or		(nr_seq_agenda_exame IS NOT NULL AND nr_seq_agenda_exame::text <> ''))
	order by 1;
	

BEGIN
			
if (ie_tipo_compl_p IS NOT NULL AND ie_tipo_compl_p::text <> '') then
	open C01;
	loop
	fetch C01 into	
		nr_seq_ageint_item_w,
		nr_seq_agenda_cons_w,
		nr_seq_agenda_exame_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		if (nr_seq_Agenda_cons_w IS NOT NULL AND nr_seq_Agenda_cons_w::text <> '') then
			select	replace(max(c.ds_email),';',',')
			into STRICT	ds_email_w
			from	agenda_consulta a,
					pessoa_fisica b,
					compl_pessoa_fisica c
			where	a.nr_sequencia			= nr_seq_agenda_cons_w
			and	a.cd_medico_req			= b.cd_pessoa_fisica
			and	b.cd_pessoa_fisica		= c.cd_pessoa_fisica
			and	c.ie_tipo_complemento		= ie_tipo_compl_p
			and	a.ie_status_agenda <> 'C'
			and	exists (SELECT	1
						from	ageint_texto_padrao_proc x
						where	x.cd_pessoa_fisica = b.cd_pessoa_fisica
						and	coalesce(x.cd_estabelecimento,cd_estabelecimento_p) = cd_estabelecimento_p
						and	x.ie_tipo_agenda in ('T','C','S'));
		else
			select	replace(max(c.ds_email),';',',')
			into STRICT	ds_email_w
			from	agenda_paciente a,
				pessoa_fisica b,
				compl_pessoa_fisica c
			where	a.nr_sequencia			= nr_seq_agenda_exame_w
			and	a.cd_medico_Exec		= b.cd_pessoa_fisica
			and	b.cd_pessoa_fisica		= c.cd_pessoa_fisica
			and	c.ie_tipo_complemento		= ie_tipo_compl_p
			and	a.ie_status_agenda <> 'C'
			and	exists (SELECT	1
						from	ageint_texto_padrao_proc x
						where	x.cd_pessoa_fisica = b.cd_pessoa_fisica
						and	coalesce(x.cd_estabelecimento,cd_estabelecimento_p) = cd_estabelecimento_p
						and	x.ie_tipo_agenda in ('E'));
			
		end if;
		if (ds_email_w IS NOT NULL AND ds_email_w::text <> '') then
			ds_retorno_w	:= ds_retorno_w || ds_email_w ||',';
		end if;
		end;
	end loop;
	close C01;
end if;

return	substr(ds_Retorno_w,1,length(ds_Retorno_w) - 1);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
