CREATE OR REPLACE FUNCTION ageint_obter_grupos_regra_proc (nr_seq_proc_interno_p bigint, nr_seq_ageint_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE

		 
nr_seq_grupo_check_list_w varchar(10);
cd_procedimento_w 	 bigint;
ie_origem_proced_w	 bigint;
ds_retorno_w		 varchar(4000);
cd_convenio_w		integer;	
cd_categoria_w		varchar(10);
cd_estabelecimento_w	smallint;
cd_plano_w		varchar(10);
		
C01 CURSOR FOR 
	SELECT	x.nr_seq_grupo_check_list 
	from	ageint_regra_checklist x 
	where	x.ie_origem_proced = ie_origem_proced_w and (x.cd_procedimento = cd_procedimento_w) or (x.nr_seq_proc_interno = nr_seq_proc_interno_p) or (x.cd_area_procedimento = obter_area_procedimento(cd_procedimento_w, ie_origem_proced_w)) or (x.cd_especialidade = obter_especialidade_proced(cd_procedimento_w, ie_origem_proced_w)) or (x.cd_grupo_proc = obter_grupo_procedimento(cd_procedimento_w, ie_origem_proced_w,'C')) 
	and	coalesce(cd_estabelecimento, cd_estabelecimento_p)	= cd_estabelecimento_p 
	order by 1;


BEGIN 
 
select	cd_estabelecimento, 
	cd_convenio, 
	cd_categoria, 
	cd_plano 
into STRICT	cd_estabelecimento_w, 
	cd_convenio_w, 
	cd_categoria_w, 
	cd_plano_w 
from	agenda_integrada 
where	nr_sequencia = nr_seq_ageint_p;
 
SELECT * FROM obter_proc_tab_interno_conv( 
				nr_seq_proc_interno_p, cd_estabelecimento_w, cd_convenio_w, cd_categoria_w, cd_plano_w, null, cd_procedimento_w, ie_origem_proced_w, null, clock_timestamp(), null, null, null, null, null, null, null, null) INTO STRICT cd_procedimento_w, ie_origem_proced_w;
 
open	c01;
loop 
fetch	c01 into nr_seq_grupo_check_list_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
 
	ds_retorno_w	:= ds_retorno_w || nr_seq_grupo_check_list_w || ',';
 
	end;
end loop;
close c01;
 
ds_retorno_w	:= substr(ds_retorno_w, 1, length(ds_retorno_w) - 1);
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
